import React, { Component } from "react";
import axios from "axios";
import {NavLink} from "react-router-dom";


class Cart extends Component {
    _isMounted = false;
  
    constructor(props) {
      super(props);
      this.state = {
          cart: []
        
      };
  
      this.props = props;
    }
  
    componentDidMount = () => {
      this._isMounted = true;
      this.fetchData();
  
    };


    fetchData = () => {
        axios
          .get("/api/cart")
          .then((response) => {
            if (this._isMounted) {
              this.setState({
                cart: response.data
              });

            }

            console.log(response)
          })
          .catch((error) => {
            console.log(error);
          });
      };
      
      render() {

        let cart = {};

        if (this.state.cart.items) {
          cart = this.state.cart.items.map((item) => (
            <NavLink to={"/shop/" + item.productId}>
            <div>{item.product_id}</div>
            <div>{item.price}</div>
            </NavLink>
          ));
        }

        



          return (
            <div>{cart}</div>
          )
      }
    }

    export default Cart;
