import React, { Component } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import FetchProductImg from "../../Components/Shop/FetchProductImg";
import $ from "jquery";
import Promocode from "../../Components/Shop/Promocode";

class Cart extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      history: []
    };

    this.props = props;
  }

  componentDidMount = () => {
    this._isMounted = true;
    this.sendGetRequest()
    

  };

  componentWillUnmount() {
    this._isMounted = false;
  }


  fetchData = () => {
    axios
      .get("/api/cart/history/" + this.state.user)
      .then((response) => {
        if (this._isMounted) {
          this.setState({
            history: response.data,
          }, console.log(this.state.history));
          
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  sendGetRequest = () => {
    this._isMounted = true;
    axios
      .get("/api/users/getUser", {withCredentials: true})
      .then((response) => {
          
        this.setState({user: response.data.user}, this.fetchData)
      })
      .catch((error) => {
        console.log(error);
      });
  };

  logout = () => {
    sessionStorage.clear()
  }

  render() {
    const CartMapping = () => {
      let cart = {};

      if (this.state.history != null && this.state.history.length < 1) {
        this.state.history = false;
      }

      if (this.state.cart) {
        cart = this.state.history.items.map((item) => (
          <li
            className="cart-container__table__products__item"
            key={item.productId + item.size}
          >
            <NavLink to={"/shop/" + item.category + "/" + item.productId}>
              <FetchProductImg className={item.model} link={item.model} />
              <div className="cart-container__table__products__item__info">
                <h2 className="cart__name">{item.productName}</h2>
                <h3 className="cart__model">Model: {item.model}</h3>
                <h3 className="cart__size">Size: {item.size}</h3>
              </div>
            </NavLink>
            <div className="cart__quantityHandler">
              <button
                className="cart__quantityHandler__add"
                onClick={() =>
                  this.handleQuantityChange(
                    item.productId,
                    item.size,
                    1,
                    item.quantity
                  )
                }
              >
                <h4>+</h4>
              </button>
              <h2 type="text" className="cart__qty">
                {item.quantity}
              </h2>
              <button
                className="cart__quantityHandler__deduct"
                onClick={() =>
                  this.handleQuantityChange(
                    item.productId,
                    item.size,
                    -1,
                    item.quantity
                  )
                }
              >
                <h4>-</h4>
              </button>
            </div>
            <h4 className="cart__total">{item.price} PLN</h4>
            <div className="cart-container__table__products__item__line"></div>
          </li>
        ));
      } else {
        cart = (
          <li className="cart-container__table__products__item__line__empty">
            The cart is empty
          </li>
        );
      }

      return cart;
    };

    return (
      <div className="cart-history-container">
          <CartMapping />
          </div>
    );
  }
}

export default Cart;
