import React from "react";
import UseForm from "../../UseForm";
import validate from "../../validateInfo";
import Aux from 'react-aux';

const inputForm = (props) => {

    const {handleChange, values, errors, handleSubmit} = UseForm(validate);

    return (
        <Aux>
        <input type={props.type} name={props.name} placeholder={props.placeholder} value={values.props.name} onChange={handleChange} />
        {errors.props.name && <p>{errors.props.name}</p>}
        </Aux>
    )

}

export default inputForm;
