import React, { Component } from "react";
import axios from "axios";
import {NavLink} from "react-router-dom";
import FetchProductImg from "../../Components/Shop/FetchProductImg";

class Cart extends Component {
    _isMounted = false;
  
    constructor(props) {
      super(props);
      this.state = {
          cart: []
        
      };
  
      this.props = props;
    }
  
    componentDidMount = () => {
      this._isMounted = true;
      this.fetchData();
  
    };

    componentWillUnmount() {
      this._isMounted = false;
    }

    componentDidUpdate = (prevProps, prevState) => {
    



      if (prevState.cart !== this.state.cart) {
        console.log(prevState.cart);
        console.log(this.state.cart)
        this._isMounted = true;
        this.fetchData();
        this._isMounted = false;
    }
    
  };


    fetchData = () => {
        axios
          .get("/api/cart")
          .then((response) => {

            if (this._isMounted) {

            

              this.setState({
                cart: response.data
              });


            console.log(response)

          }
          })
          .catch((error) => {
            console.log(error);
          });
          
      };

    emptyCart = () => {      

      this._isMounted = true;

        axios
          .delete("/api/cart/empty-cart")
          .then((response) => {


            if (response.data != null && response.data < 1) {
            response.data = response.data
            
            } else {
              response.data = false
            }

              this.setState({
                cart: response.data
            })

          

            console.log(response)

            


          })
          .catch((error) => {
            console.log(error);
          });

          console.log("empty clicked")


          


          
    };
      
      
      render() {


        
        const CartMapping = () => {

          let cart = [];

          if (this.state.cart != null && this.state.cart.length < 1) {   this.state.cart = false  } 

        if (this.state.cart) {
          cart = (this.state.cart.items.map((item) => (
            <li className="cart-container__table__products__item" key={item.productId + item.size}>
            <NavLink to={"/shop/" + item.category + "/" + item.productId} >
                      <FetchProductImg
            className={item.model}
            link={item.model}
        />

            <div className="cart__qty">{item.quantity}</div>
            <div className="cart__model">{item.model}</div>
            <div className="cart__size">{item.size}</div>
            <div className="cart__total"> {item.total}</div>
            </NavLink>
            </li>
          )))
        } else {
          cart = (<div>The cart is empty</div>) 
            
          }

          return cart
        }

      

          return (
            <div className="cart-container">
            <div classNmae="cart-container__tooltip">
              <h1>Cart</h1>
              <ul className="cart-container__tooltip__icons">
                <li></li>
                <li></li>
                <li></li>
              </ul>
            </div>

            <div className="cart-container__table">
            
            <ul className="cart-container__table__legend">
              <li className="cart-container__table__legend__item">Item</li>
              <li className="cart-container__table__legend__quantity">Quantity</li>
              <li className="cart-container__table__legend__subtotal">Subtotal</li>
            </ul>
            <ul className="cart-container__table__products">

            <CartMapping />
            </ul>

            <h2>Total: {() => {if (this.state.cart) {return this.state.cart.subTotal} else { return 0}}}</h2>
            <button className="cart-container__table__continue-shopping">Continue shopping </button>
            <button className="cart-container__table__checkout">Proceed to checkout</button>
            </div>
            </div>
          )
      }
    }

    export default Cart;
