import React, { Component } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import $ from "jquery";
import InputForm from "./InputForm";

class Checkout extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      cart: []
    };

    this.props = props;
  }

  componentDidMount = () => {
      this.sendGetRequest()
      this.fetchData()
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  componentDidUpdate = (prevProps, prevState) => {
    if (prevProps.match.params.step !== this.props.match.params.step) {
      this._isMounted = true;
      this.sendGetRequest();
      this.fetchData()
    }
  };

  sendGetRequest = () => {
    this._isMounted = true;
    axios
      .get("/api/users/getUser")
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  fetchData = () => {
    axios
      .get("/api/cart")
      .then((response) => {
        if (this._isMounted) {
          this.setState({
            cart: response.data,
          });
          $(".items-counter").text(response.data.itemsTotal);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {

    const CartMapping = () => {
        let cart = {};
  
        if (this.state.cart != null && this.state.cart.length < 1) {
          this.state.cart = false;
        }
  
        if (this.state.cart) {
          cart = this.state.cart.items.map((item) => (
            <li
              key={item.productId + item.size}
            >
                <div className="cart__info">
                  <h2 className="cart__info__name">{item.productName}</h2>
                  <h3 className="cart__info__desc">Model: {item.model}, Size: {item.size}</h3>
                  
                </div>

                <h2 className="cart__items">{item.quantity > 1 ? item.quantity + " items" : item.quantity + " item"}</h2>
              
              <h2 className="cart__total">{item.price} PLN</h2>
              <div className="cart__line"></div>
            </li>
          ));
        } else {
          cart = (
            <li className="cart-container__table__products__item__line__empty">
              The cart is empty
            </li>
          );
        }
  
        return cart;
      };

    return (
      <div className="checkout">
      <div className="checkout__form">
      <InputForm
            status={this.state.loginStatus}
            handler={this.handleRequest}
            section="checkout"
          > </InputForm>
      </div>
      <div className="checkout__summary">
          <h4 className="checkout__summary__title">Items</h4>
          <ul className="checkout__summary__list">
          {<CartMapping />}
          </ul>
          <div className="checkout__summary__total">
              <h2 className="checkout__summary__total__title">Total</h2>
              <h2 className="checkout__summary__total__items">
                {this.state.cart.itemsTotal || 0}{" "}
                {this.state.cart.itemsTotal == 1 ? "item" : "items"}
              </h2>
              <h4 className="checkout__summary__total__value">
                {this.state.cart.subTotal + this.state.cart.discountTotal || 0} PLN
              </h4>
            </div>
            <NavLink className="checkout__summary__edit" to="shop/cart"><h3 >Edit cart</h3></NavLink>
      </div>
      </div>
    );
  }
}

export default Checkout;
