import React, { Component } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import FetchProductImg from "../../Components/Shop/FetchProductImg";
import $ from "jquery";
import Promocode from "../../Components/Shop/Promocode";

class Cart extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      history: [],
    };

    this.props = props;
  }

  componentDidMount = () => {
    this._isMounted = true;
    this.sendGetRequest();
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  fetchData = () => {
    axios
      .get("/api/cart/history/" + this.state.user)
      .then((response) => {
        if (this._isMounted) {
          console.log(response.data);
          this.setState(
            {
              history: response.data,
            },
            console.log(this.state.history)
          );
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  sendGetRequest = () => {
    this._isMounted = true;
    axios
      .get("/api/users/getUser", { withCredentials: true })
      .then((response) => {
        console.log(response.data);
        this.setState({ user: response.data.username }, this.fetchData);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  logout = () => {
    sessionStorage.clear();
  };

  render() {
    const CartMapping = () => {
      if (this.state.history != null && this.state.history.length < 1) {
        this.state.history = false;
      }
      let cart;
      if (this.state.history) {
        cart = this.state.history.map((cart) => (
          <li className="profile-cart" key={cart._id}>
            <div className="profile-cart-summary"></div>

            <ul>
              {() => {
                const ListMapping = () => {
                  const list = cart.items.map((item) => (
                    <li
                      className="cart-container__table__products__item"
                      key={item.productId + item.size}
                    >
                      <NavLink
                        to={"/shop/" + item.category + "/" + item.productId}
                      >
                        <FetchProductImg
                          className={item.model}
                          link={item.model}
                        />
                        <div className="cart-container__table__products__item__info">
                          <h2 className="cart__name">{item.productName}</h2>
                          <h3 className="cart__model">Model: {item.model}</h3>
                          <h3 className="cart__size">Size: {item.size}</h3>
                        </div>
                      </NavLink>
                      <h4 className="cart__total">{item.price} PLN</h4>
                      <div className="cart-container__table__products__item__line"></div>
                    </li>
                  ));

                  return list;
                };

                return <ListMapping />
              }}
            </ul>
          </li>
        ));
        return cart;
      } else {
        return <li></li>;
      }
    };

    return (
      <div className="cart-history-container">
        <CartMapping />
      </div>
    );
  }
}

export default Cart;
