import React from "react";
import UseForm from "../../UseForm";
import validate from "../../validateInfo";
import Aux from "react-aux";
import $ from "jquery";

const inputForm = (props) => {
  const { handleChange, values, errors, handleSubmit } = UseForm(validate);

  const type = props.type;
  const name = props.name;

  function buttonClickHandler() {
    handleSubmit();

    console.log(errors[name] === "")


    if (errors[name] === "" || $(`input[name=${name}]`).val() !== "") {
    props.handler()

    }
  }


  if (props.section === "login") {


  return (
    <Aux>
      <label className="cart-login__form-container__email">
            <h3>E-mail:</h3>
      <input
        type="text"
        name="email"
        placeholder="Enter your email"
        value={values.name}
        onChange={handleChange}
      />
      <p>{errors["email"]}</p>
      </label>
      

      <label className="cart-login__form-container__password">
            <h3>Password:</h3>
      <input
        type="password"
        name="password"
        placeholder="Enter password"
        value={values.name}
        onChange={handleChange}
      />
      </label>
      <p>{errors["password"]}</p>

      <button className="cart-login__form-container__login" onClick={buttonClickHandler}>{props.children}</button>
      
    </Aux>
  );


}

else {
  return (
    <Aux>
      <input
        type={type}
        name={name}
        placeholder={props.placeholder}
        value={values.name}
        onChange={handleChange}
      />
      <button className={props.class} onClick={buttonClickHandler}>{props.children}</button>
      <p>{errors[name]}</p>
    </Aux>
  );
}
};

export default inputForm;
