import React, {useEffect, useRef} from "react";
import { Slider, RangeSlider } from 'rsuite';

function PriceSlider (props) {
    const [value, setValue] = React.useState([props.minValue, props.maxValue]);
    const startFunction = props.clickHandlerStart
    const endFunction = props.clickHandlerEnd

    useEffect(() => {

      handler().bind(this)


   }, [value]);

   function handler () {
    props.clickHandlerStart()
    props.clickHandlerEnd()
  }

    return (
      <div className="price-slider">
        <div className="price-slider__range">
          <RangeSlider
            progress
            style={{ marginTop: 16 }}
            value={value}
            step={(props.maxValue - props.minValue)/100}
            min={props.minValue}
            max={props.maxValue}
            onChange={value => {
              setValue(value);
            }}
            onClick={props.clickHandler}
          />
        </div>
        <div className="price-slider__inputs">
            <input
            className="price-slider__inputs__start price-slider__inputs__child"
              min={props.minValue}
              max={props.maxValue}
              name="price__start"
              value={value[0]}
              onChange={nextValue => {
                const [start, end] = value;
                
                setValue([+nextValue.target.value, end]);

              }}
              
            />
            <h2>to</h2>
            <input
            className="price-slider__inputs__end price-slider__inputs__child"
              min={props.minValue}
              max={props.maxValue}
              name="price__end"
              value={value[1]}
              onChange={nextValue => {
                const [start, end] = value;
                
                setValue([start, +nextValue.target.value]);


              }}

            />
        </div>
      </div>
    );
  }

  export default PriceSlider;
