import React from "react";
import $ from "jquery";
import {NavLink} from 'react-router-dom';

const topMenu = () => {

    const switchMenuHandler = () => {

        $('.top-menu').toggleClass('active inactive');
    }

    $('body').on('click', (e) => {

      console.log(!$(e.target).is('.top-menu'))
      if (!$('.top-menu').has(e.target).length > 0) {
        $('.top-menu').removeClass('active').addClass('inactive');
      }
    })

  return (

    <div className="top-menu inactive">
      <div className="top-menu__icon" onClick={switchMenuHandler}>
      <div className="top-menu__icon-1"></div>
          <div className="top-menu__icon-2"></div>
      </div>
      <ul className="top-menu__list">
      <li><NavLink exact to="/" activeClassName='is-active'  className="menu-font link-2">home</NavLink></li>
      <li><NavLink to="/shop/all" activeClassName='is-active' className="menu-font link-2">shop</NavLink></li>
      <li><NavLink to="/about"activeClassName='is-active' className="menu-font link-2">about us</NavLink></li>
      <li><NavLink to="/terms" activeClassName='is-active' className="menu-font link-2">terms & shipment</NavLink></li>
      <li><NavLink to="/contact" activeClassName='is-active'className="menu-font link-2">contact us</NavLink></li>
      </ul>

    </div>
    
  );
};

export default topMenu;
