import React, { Component } from "react";
import axios from "axios";
import FetchProductImg from "./FetchProductImg";
import {withRouter} from "react-router-dom";


class ProductItem extends Component {


    componentDidMount = () => {
        const category = this.props.match.params.category;
        this.fetchData(category);
      };
    
      componentDidUpdate = (prevProps, prevState) => {
          this.fetchData(this.props.match.params.category)
      }
      
    
    
      fetchData = (category) => {
        axios
          .get("/api/products")
          .then((response) => {
    
            this.setState({
              products: response.data.filter((filteredProduct) => {
                return (
                  filteredProduct.category ===
                  (category === "all"
                    ? filteredProduct.category
                    : category)
                );
              }),
            });
          })
          .catch((error) => {
            console.log(error);
          });
      };
render() {


  return (
    <div className="product-item-container">
        <FetchProductImg
          className={props.object.product_id}
          link={props.object.product_id}
        />
        <h1>{props.object.title}</h1>
    </div>
  );

}
};

export default withRouter(ProductItem)
