import React, { Component } from "react";
import axios from "axios";
import { withRouter, NavLink } from "react-router-dom";
import $ from "jquery";
import InputForm from "./InputForm";

class Checkout extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      cart: [],
      step: this.props.match.params.step,
      checked: ""
    };

    this.props = props;
  }

  componentDidMount = () => {
    this.sendGetRequest();
    this.fetchData();
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  componentDidUpdate = (prevProps, prevState) => {
    if (prevProps.match.params.step !== this.props.match.params.step) {
      console.log("adfasf");
      this._isMounted = true;
      this.sendGetRequest();
      this.fetchData();
      this.setState(
        { step: this.props.match.params.step })
    }
  };

  sendGetRequest = () => {
    this._isMounted = true;
    axios
      .get("/api/users/getUser")
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  fetchData = () => {
    axios
      .get("/api/cart")
      .then((response) => {
        if (this._isMounted) {
          this.setState({
            cart: response.data,
          }, this.autocompletePast);
          $(".items-counter").text(response.data.itemsTotal);

          
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };
  
  handleRadioChange = () => {

    if (document.querySelector('input[name="Parcel Locker InPost"]').checked == false) {
      return "Else";

    } else {
      return "InPost"
    }
  

}

  autocompletePast = () => {
    if (this.state.cart.personalData !== undefined) {

      console.log(this.state.cart.personalData.firstName)
      $('.checkout__form__name input[name="fname"]').val(this.state.cart.personalData.firstName)
      $('.checkout__form__surname input[name="lname"]').val(this.state.cart.personalData.lastName)
      $('.checkout__form__email input[name="email"]').val(this.state.cart.personalData.email)
      $('.checkout__form_phone input[name="phone"]').val(this.state.cart.personalData.phone)
      $('.checkout__form__country input[name="country"]').val(this.state.cart.personalData.country)
      $('.checkout__form__code input[name="postal-code"]').val(this.state.cart.personalData.postalCode)
      $('.checkout__form__city input[name="address-level2"]').val(this.state.cart.personalData.city)
      $('.checkout__form__address-line1 input[name="address-line1"]').val(this.state.cart.personalData.addressLine1)
      $('.checkout__form__address-line2 input[name="address-line2"]').val(this.state.cart.personalData.addressLine2)

    } 
  }

  handleShipAddressToggle = () => {

      if ($('.switch input').is(':checked')) {
        $('.checkout__form___shipping__country input[name="country"]').val(this.state.cart.personalData.country)
        $('.checkout__form__shipping__code input[name="postal-code"]').val(this.state.cart.personalData.postalCode)
        $('.checkout__form__shipping__city input[name="address-level2"]').val(this.state.cart.personalData.city)
        $('.checkout__form__shipping__address-line1 input[name="address-line1"]').val(this.state.cart.personalData.addressLine1)
        $('.checkout__form__shipping__address-line2 input[name="address-line2"]').val(this.state.cart.personalData.addressLine2)
      } else {
        $('.checkout__form___shipping__country input[name="country"]').val("")
        $('.checkout__form__shipping__code input[name="postal-code"]').val("")
        $('.checkout__form__shipping__city input[name="address-level2"]').val("")
        $('.checkout__form__shipping__address-line1 input[name="address-line1"]').val("")
        $('.checkout__form__shipping__address-line2 input[name="address-line2"]').val("")
      }
      console.log("afdasdfasdfasfd")
  }

  postPersonalData = () => {
    axios
      .post("/api/cart/post-personal-data", {
        id: this.state.cart._id,
        firstName: $('input[name="fname"]').val(),
        lastName: $('input[name="lname"]').val(),
        email: $('input[name="email"]').val(),
        phone: $('input[name="phone"]').val(),
        country: $('input[name="country"]').val(),
        postalCode: $('input[name="postal-code"]').val(),
        city: $('input[name="address-level2"]').val(),
        addressLine1: $('input[name="address-line1"]').val(),
        addressLine2: $('input[name="address-line2"]').val(),
      })
      .then((response) => {
        console.log(response.data);
        console.log(this.state.cart._id);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  postShippingData = () => {
    axios
      .post("/api/cart/post-shipping-data", {
        id: this.state.cart._id,
        method: $("input[name='delivery']:checked").val(),
        address: "address",
        price: $("input[name='delivery']:checked").next(".delivery__price"),
      })
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  postCart = () => {
    axios
      .post("/api/cart/post-cart-db")
      .then((response) => {
        if (this._isMounted) {
          this.setState({
            cart: response.data,
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // renderShippingDetails = (target) => {

  //   const myValue = target.value;
  //   this.setState({
  //     checked: myValue
