import React, { Component } from "react";
import axios from "axios";
import FetchProductImg from "./FetchProductImg";
import { withRouter } from "react-router-dom";
import $ from "jquery";
import Aux from "react-aux";
import PopUp from "./Popup";
import InputForm from "./InputForm";

class ProductItem extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      product: [],
      stock: [],
    };

    this.props = props;
  }

  // componentDidMount = () => {
  //   this._isMounted = true;
  //   const id = this.props.match.params.id;
  //   this.fetchItem(id);
  //   console.log(this.props.match.params.id);
  // };

  // componentDidUpdate = (prevProps, prevState) => {
  //   if (this._isMounted) {
  //     this.fetchItem(this.props.match.params.id);
  //   }
  // };

    fetchItem = (id) => {

    let localStorageData = JSON.parse(localStorage.getItem('products'));

        if (this._isMounted) {

          console.log(localStorageData)

          
          this.setState({
            product: localStorageData.filter((filteredProduct) => {
              return (
                filteredProduct._id === id
              )
            }, this.setState({
              stock: this.state.product.stock,
            }))
          })  

          
        }
      }
      
  

  SizePickerHandler = () => {
    $(".product-item-container__info__size-picker__arrow").toggleClass(
      "active"
    );
    $(".product-item-container__info__size-picker__list").toggleClass("active");
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  handleSizeClick(event) {
    if (
      $(event.target).hasClass("notify") ||
      $(event.target).hasClass("pop") ||
      $(event.target).is("#pop__svg")
    ) {
      $(".pop").toggleClass("active");
      $(".product-item-container__info__size-picker__arrow").toggleClass(
        "active"
      );
      $(".product-item-container__info__size-picker__list").toggleClass(
        "active"
      );
    }

    if ($(event.target).hasClass("value")) {
      console.log(event.target);
      console.log($(event.target).text());
      $(".product-item-container__info__size-picker__default").text(
        $(event.target).text()
      );

      $(".product-item-container__info__size-picker__arrow").toggleClass(
        "active"
      );
      $(".product-item-container__info__size-picker__list").toggleClass(
        "active"
      );
    }
  }

  postNotify(event) {
    $(".pop").toggleClass("active");
  }

  render() {
    

    return (
      <Aux>
        <div className="product-item-container" key={this.state.product._id}>
          <button className="product-item-container__back" onClick={this.props.history.goBack}><h2>ðŸ¡  &nbsp; back to {this.state.product.category}</h2></button>
          <div className="product-item-container__toggle-product">
            <button className="previous"><h2>previous</h2></button>
            <h2> / </h2>
            <button className="next"><h2>next</h2></button>
          </div>
          <FetchProductImg
            className={this.state.product.product_id}
            link={this.state.product.product_id}
          />
          <div className="product-item-container__info">
            <div className="product-item-container__info__main">
              <h4>{this.state.product.product_name}</h4>
              <h4 className="product-item-container__info__main__price">
                {this.state.product.price} PLN
              </h4>
              <h3>Model: {this.state.product.product_id}</h3>
            </div>
            <div className="product-item-container__info__desc">
              <h2>Specifications: {this.state.product.specs}</h2>
              <h2>Material: {this.state.product.material}</h2>
              <h2>Collection: {this.state.product.collection_name}</h2>
            </div>
            {/* {SizePicker()} */}
            <button className="product-item-container__info__button">
              Add to cart
            </button>
          </div>
        </div>

        <PopUp popClass="notify" handler={this.handleSizeClick}>
          <h2>
            Enter your e-mail address and we we will notify you when the item is
            available.
          </h2>
          <InputForm
            handler={this.postNotify}
            class="pop__content__button pop"
            type="text"
            name="email"
            placeholder="Enter your email"
          >
            <svg
              version="1.1"
              id="pop__content__button__svg"
              xmlns="http://www.w3.org/2000/svg"
              x="0px"
              y="0px"
              width="408px"
              height="408px"
              viewBox="0 0 408 408"
              enableBackground="new 0 0 408 408;"
            >
              <path d="M408,178.5H96.9L239.7,35.7L204,0L0,204l204,204l35.7-35.7L96.9,229.5H408V178.5z" />
            </svg>
          </InputForm>
        </PopUp>
      </Aux>
    );
  }
}

export default withRouter(ProductItem);
