import React, { Component } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import FetchProductImg from "../../Components/Shop/FetchProductImg";
import $ from "jquery";
import Promocode from "../../Components/Shop/Promocode";

class Profile extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      cartHistory: [],
      carts: [],
    };

    this.props = props;
  }

  componentDidMount = () => {
    this._isMounted = true;
    this.sendGetRequest();
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  componentDidUpdate = (prevProps, prevState) => {
    if (prevState.cartHistory != this.state.cartHistory) {
      this._isMounted = true;
      this.fetchData();

      console.log("afasfasdf");
    }
  };

  fetchData = () => {
    axios
      .get("/api/cart/history/" + this.state.user)
      .then((response) => {
        if (this._isMounted) {
          console.log([response.data]);
          this.setState(
            {
              cartHistory: [response.data],
            },
            () => {
              this._isMounted = false;
            }
          );
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  sendGetRequest = () => {
    this._isMounted = true;
    axios
      .get("/api/users/getUser", { withCredentials: true })
      .then((response) => {
        console.log(response.data);
        this.setState({ user: response.data.username }, this.fetchData);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  logout = () => {
    sessionStorage.clear();
  };

  render() {
    if (this.state.cartHistory.length === 0) return null;
    let carts = [];

    carts = this.state.cartHistory[0].map((item) => {
      return (
        <li className="profile-cart" key={item._id}>
          <h3>{item._id}</h3>
          <h3>{item.createdAt}</h3>
          <h3>{item.items}</h3>
          <h3>{item.total}</h3>
        </li>
      );
    });

    return <ul className="cart-history-container">{carts}</ul>;
  }
}

export default Profile;
