import React, { Component } from "react";
import axios from "axios";
import FetchProductImg from "./FetchProductImg";
import { withRouter } from "react-router-dom";
import $ from 'jquery';

class ProductItem extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      product: [],
      stock: []
    };

    this.props = props;
  }

  componentDidMount = () => {
    this._isMounted = true;
    const id = this.props.match.params.id;
    this.fetchItem(id);
    console.log(this.props.match.params.id);
  };

  componentDidUpdate = (prevProps, prevState) => {
    if (this._isMounted) {
      this.fetchItem(this.props.match.params.id);
    }
  };

  fetchItem = (id) => {
    axios
      .get("/api/products/" + id)
      .then((response) => {
        if (this._isMounted) {
          this.setState({
            product: response.data,
          });
          this.setState(({
            stock: this.state.product.stock
          }))
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  SizePickerHandler = () => {
    $('.product-item-container__info__size-picker__arrow').toggleClass('active');
    $('.product-item-container__info__size-picker__list').toggleClass('active');

   
  }

  componentWillUnmount() {
    this._isMounted = false;
  }


  handleSizeClick(event) {

    console.log(event.target)
    console.log($(event.target).text());
    $('.product-item-container__info__size-picker__default').text($(event.target).text())
  }

  render() {

    const stock = this.state.stock.map( function(stockItem) {


      

    });

    return (
      <div className="product-item-container" key={this.state.product._id}>
        <FetchProductImg
          className={this.state.product.product_id}
          link={this.state.product.product_id}
        />
        <div className="product-item-container__info">
          <div className="product-item-container__info__main">
            <h4>{this.state.product.product_name}</h4>
            <h4 className="product-item-container__info__main__price">{this.state.product.price} PLN</h4>
            <h3>Model: {this.state.product.product_id}</h3>
          </div>
          <div className="product-item-container__info__desc">
            <h2>Specifications: {this.state.product.specs}</h2>
            <h2>Material: {this.state.product.material}</h2>
            <h2>Collection: {this.state.product.collection_name}</h2>
          </div>
          <div className="product-item-container__info__size-picker">
            <h2 onClick={this.SizePickerHandler} className="product-item-container__info__size-picker__title">Size: </h2>
            <h2 onClick={this.SizePickerHandler} className="product-item-container__info__size-picker__default">Select</h2>
            <div onClick={this.SizePickerHandler} className="product-item-container__info__size-picker__arrow"></div>
            <ul className="product-item-container__info__size-picker__list">

            { () => {if({stock}.quantity === "0") {
         <li key={stock.size.toString()} className="unavailable"><h2 >{stock.size}</h2><h2 className="notify">Notify when available</h2></li>
      } else {
        <li key={stock.size.toString()} onClick={this.handleSizeClick} className="available"><h2>{stock.size}</h2></li>
      }}}
      </ul>
      </div>
      </div>
          <button className="product-item-container__info__button" >Add to cart</button>
        </div>

    );
  }
}

export default withRouter(ProductItem);
