import React, { Component } from "react";
import axios from "axios";
import {NavLink} from "react-router-dom";
import FetchProductImg from "../../Components/Shop/FetchProductImg";

class Cart extends Component {
    _isMounted = false;
  
    constructor(props) {
      super(props);
      this.state = {
          cart: []
        
      };
  
      this.props = props;
    }
  
    componentDidMount = () => {
      this._isMounted = true;
      this.fetchData();
  
    };



    componentDidUpdate = (prevProps, prevState) => {
    

      if (prevState.cart !== this.state.cart) {
        console.log(prevState.cart !== this.state.cart);

        this._isMounted = true;
        this.fetchData();
        
        
    }

  };


  componentWillUnmount() {
    this._isMounted = false;
  }


    fetchData = () => {
        axios
          .get("/api/cart")
          .then((response) => {

            if (this._isMounted) {


              this.setState({
                cart: response.data
              });


            console.log(response)

          }
          this._isMounted = false;
          })
          .catch((error) => {
            console.log(error);
          });
      };

    emptyCart = () => {      

      this._isMounted = true;

        axios
          .delete("/api/cart/empty-cart")
          .then((response) => {

            if (this._isMounted) {

              if (response.data === null) {
                this.setState({
                  cart: []
              })
              } else {
                this.setState({
                  cart: response.data
              })
              }


          }
          })
          .catch((error) => {
            console.log(error);
          });


     this._isMounted = false;     

          
    };
      
      
      render() {


        
        const CartMapping = () => {

        if (this.state.cart.length !== 0) {
          return (this.state.cart.items.map((item) => (
            <li className="cart-container__products__item" key={item.productId + item.size}>
            <NavLink to={"/shop/" + item.category + "/" + item.productId} >
                      <FetchProductImg
            className={item.model}
            link={item.model}
        />
            <div>{item.productId}</div>
            <div>{item.price}</div>
            <div>{item.quantity}</div>
            <div>{item.model}</div>
            <div>{item.total}</div>
            </NavLink>
            </li>
          )))
        } else {
          return (<div>The cart is empty</div>) 
            
          }
        }

      

          return (
            <div className="cart-container">
            <h2>Total: {() => {if (this.state.cart.length !== 0)  {return this.state.cart.subTotal} else {return 0}}}</h2>
            <ul className="cart-container__products">
            <CartMapping />
            </ul>
            <button onClick={this.emptyCart} className="cart-container__empty">Empty cart</button>
            </div>
          )
      }
    }

    export default Cart;
