import React, {Component} from 'react';
import NewsletterInfo from "../../Components/NewsletterInfo/Newsletter_Info";
import BottomInfo from "../../Components/BottomInfo/BottomInfo";
import TopMenu from "../../Components/TopMenu/TopMenu";
import ProductList from "../../Components/Shop/ProductList";
import TopImg from "../../Components/Shop/topImg.png";
import axios from "axios";
import FetchProductImg from "../../Components/Shop/FetchProductImg";
import { withRouter } from "react-router-dom";
import Aux from "react-aux";
import TopTooltip from "../../Components/Shop/TopTooltip";

class Shop extends Component {


  constructor(props) {
    super(props);
    this.state = {
      products: [],
      filteredProducts: [],
      returnLocation: ""
    }
    };


  componentDidMount () {
    this.fetchData();
  }

  componentWillReceiveProps(nextProps) {
    this.setState({ returnLocation: nextProps.returnLocation });  
  }

  fetchData = () => {
    axios
      .get("/api/products")
      .then((response) => {
        console.log(response.data);
        console.log(this.props.location.pathname);
        this.setState({
          products: response.data.filter((filteredProduct) => {
            return (
              filteredProduct.category ===
              (this.props.match.params.category === "all"
                ? filteredProduct.category
                : this.props.match.params.category)
            );
          }),
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };
  

  render() {


    const products = this.state.products.map((product) => (
      <div className="products-container__list__product" key={product._id}>
        <FetchProductImg
          className={product.product_id}
          link={product.product_id}
        />
        <div className="products-container__list__product__label">
          <h2>{product.product_name}</h2>
          <h3>{product.price} PLN</h3>
        </div>
      </div>
    ));
    
    return (

      

        <div className="shop">


          
          <div>{this.props.locationId}</div>
          
          <img className="shop__topImg" src={TopImg} alt="graphics"/>
          <NewsletterInfo />
          <BottomInfo />
          <TopMenu />

          <TopTooltip />;
        <div className="products-container">
          <div className="products-container__tooltip">
            <h4>collection</h4>
          </div>
          <div className="products-container__list">{products}</div>
        </div>


          

        </div>


    );
  }
}

export default withRouter(Shop);
