import React, { Component } from "react";
import axios from "axios";
import {NavLink} from "react-router-dom";
import FetchProductImg from "../../Components/Shop/FetchProductImg";

class Cart extends Component {
    _isMounted = false;
  
    constructor(props) {
      super(props);
      this.state = {
          cart: []
        
      };
  
      this.props = props;
    }
  
    componentDidMount = () => {
      this._isMounted = true;
      this.fetchData();
  
    };


    fetchData = () => {
        axios
          .get("/api/cart")
          .then((response) => {
            if (this._isMounted) {
              this.setState({
                cart: response.data
              });

            }

            console.log(response)
          })
          .catch((error) => {
            console.log(error);
          });
      };
      
      render() {


        
        const CartMapping = () => {

        let cart
        if (this.state.cart.items) {
          cart = this.state.cart.items.map((item) => (
            <li className="cart-container__products__item">
            <NavLink to={"/shop/" + item.productId} key={item.productId + item.size}>
                      <FetchProductImg
            className={item.model}
            link={item.model}
        />
            <div>{item.productId}</div>
            <div>{item.price}</div>
            <div>{item.quantity}</div>
            <div>{item.model}</div>
            <div>{item.total}</div>
            </NavLink>
            </li>
          ));
        } else {
          cart =  () => {return (<div>The cart is empty</div>) }
            
          }

          return cart

        }

      

          return (
            <div className="cart-container">
            <h2>Total: {this.state.cart.subTotal}</h2>
            <ul className="cart-container__products">
            <CartMapping />
            </ul>
            
            </div>
          )
      }
    }

    export default Cart;
