import React, { Component } from "react";
import axios from "axios";
import FetchProductImg from "./FetchProductImg";
import {withRouter} from "react-router-dom";


class ProductItem extends Component {

    _isMounted = false;

    constructor(props) {
        super(props);
        this.state = {
          product: [],
          
        };
    
        this.props = props;
      }

    componentDidMount = () => {

        this._isMounted = true;
        const id = this.props.match.params.id;
        this.fetchItem(id);
        console.log(this.props.match.params.id)
        
      };
    
      componentDidUpdate = (prevProps, prevState) => {

        if (this._isMounted) {

        
          this.fetchItem(this.props.match.params.id)

        }
      }
      
    
    
      fetchItem = (id) => {
        axios
          .get("/api/products/" + id)
          .then((response) => {
            if (this._isMounted) {
            this.setState({
              product: response.data
              
            });
        }
        })
          .catch((error) => {
            console.log(error);
          });
      };

      componentWillUnmount() {
        this._isMounted = false;
      }
      
render() {


  return (
    <div className="product-item-container">
        <FetchProductImg
          className={this.state.product.product_id}
          link={this.state.product.product_id}
        />
        <div className="product-item-container__info">
        <h1>{this.state.product.title}</h1>
        <h1>{this.state.product.price} PLN</h1>
        <button className="product-item-container__info__button"></button>
        </div>
        

    </div>
  );

}
};

export default withRouter(ProductItem)
