import React, { Component } from "react";
import axios from "axios";
import {NavLink} from "react-router-dom";


class Cart extends Component {
    _isMounted = false;
  
    constructor(props) {
      super(props);
      this.state = {
          cart: []
        
      };
  
      this.props = props;
    }
  
    componentDidMount = () => {
      this._isMounted = true;
      this.fetchData();
  
    };


    fetchData = () => {
        axios
          .get("/api/cart")
          .then((response) => {
            if (this._isMounted) {
              this.setState({
                cart: response.data
              });

            }

            console.log(response)
          })
          .catch((error) => {
            console.log(error);
          });
      };
      
      render() {

        let cart = {};
        
        const cartMapping = () => {

        
        if (this.state.cart.items) {
          return cart = this.state.cart.items.map((item) => (
            <NavLink to={"/shop/" + item.productId} key={item.productId + item.size}>
            <div>{item.product_id}</div>
            <div>{item.price}</div>
            </NavLink>
          ));
        } else {
          return cart =  () => {return (<div>The cart is empty</div>) }
            
          }

        }

      

          return (
            <div>{cartMapping}</div>
          )
      }
    }

    export default Cart;
