import React, { Component } from "react";
import axios from "axios";
import FetchProductImg from "./FetchProductImg";
import { withRouter } from "react-router-dom";

class ProductItem extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      product: [],
    };

    this.props = props;
  }

  componentDidMount = () => {
    this._isMounted = true;
    const id = this.props.match.params.id;
    this.fetchItem(id);
    console.log(this.props.match.params.id);
  };

  componentDidUpdate = (prevProps, prevState) => {
    if (this._isMounted) {
      this.fetchItem(this.props.match.params.id);
    }
  };

  fetchItem = (id) => {
    axios
      .get("/api/products/" + id)
      .then((response) => {
        if (this._isMounted) {
          this.setState({
            product: response.data,
          });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  componentWillUnmount() {
    this._isMounted = false;
  }

  render() {

    const stock = this.state.product.stock.map((stockItem) => (

      <div>{stockItem.size}</div>

    ));

    return (
      <div className="product-item-container">
        <FetchProductImg
          className={this.state.product.product_id}
          link={this.state.product.product_id}
        />
        <div className="product-item-container__info">
          <div className="product-item-container__info__main">
            <h4>{this.state.product.product_name}</h4>
            <h4 className="product-item-container__info__main__price">{this.state.product.price} PLN</h4>
            <h3>Model: {this.state.product.product_id}</h3>
          </div>
          <div className="product-item-container__info__desc">
            <h2>Specifications: {this.state.product.specs}</h2>
            <h2>Material: {this.state.product.material}</h2>
            <h2>Collection: {this.state.product.collection_name}</h2>
          </div>
          <div className="product-item-container__info__size-picker">
            <h2 className="product-item-container__info__size-picker__title">Size: </h2>
            <div className="product-item-container__info__size-picker__arrow"></div>
            {/* {stock} */}
          </div>
          <button className="product-item-container__info__button">Add to cart</button>
        </div>
      </div>
    );
  }
}

export default withRouter(ProductItem);
